import PathRegExp from 'path-to-regexp';
// const ROUTE_MAP = new Map();
export const ROUTE_MAP = new Map();

/**
 * Generates a express style regexp for a given route and stores in a Map
 * @param  {string} route The route that should be converted into a regexp
 * @return {Object}       Returns an object with param keys and a path regexp
 */
export function getParameterizedPath(route, options = { useMap: true, }) {
  if (ROUTE_MAP.has(route) && options.useMap) {
    return ROUTE_MAP.get(route);
  } else {
    var keys = [];
    var result = new PathRegExp(route, keys);
    // console.log({ route, }, {
    //   re: result,
    //   keys,
    // });
    ROUTE_MAP.set(route, {
      re: result,
      keys: keys,
    });
    return { keys:keys, re: result, };
  }
}


/**
 * Finds a matching dynamic route from manifest
 * @param  {Object|Array} routes   The manifest configuration object
 * @param  {string} location The window location that should be resolved
 * @return {string}          A matching dynamic route
 */
export function findMatchingRoutePath(routes, location, options = {}) {
  const { return_matching_keys, } = options;
  let matching;
  let params;
  let re;
  location = (/\?[^\s]+$/.test(location)) ? location.replace(/^([^\s\?]+)\?[^\s]+$/, '$1') : location;
  const routeArray = Array.isArray(routes) ? routes : Object.keys(routes);
  routeArray.forEach(function(key){
    var result = getParameterizedPath(key, options);
    if (result.re.test(location) && !matching) {
      matching = key;
      re = result.re;
      if (return_matching_keys) {
        const matchingParams = result.re.exec(matching);
        const matchingVals = location.match(result.re);
        params = matchingVals.reduce((result, val, idx) => { 
          if (idx !== 0 && typeof val === 'string') result[ matchingParams[ idx ].replace(':','') ] = val;
          return result;          
        }, {});
      }
    }
  });
  // console.log({ routes, location, matching, });
  return return_matching_keys && matching ? {
    route: matching,
    location,
    params,
    re,
  }
    : matching;
}

# test-matching-route
Test a location against a list of possible routes. This is a simple way to test routes.

## Example

This works for both socket servers and clients
```javascript
// SERVER CODE
import { findMatchingRoutePath, } from 'test-matching-route';

const routesObject = {
  '/collections/:collection/products/:product': { page:'data1', },
  '/products/:product': { page:'data2',  },
  '/somedata/:collection/products/:id': { page:'data3', },
  '/:product': { page:'data4',  },
  '/:id': { page:'data5', },
};
const routesArray = ['/collections/:collection/products/:product',  '/products/:product',  '/somedata/:collection/products/:id',  '/:product',  '/:id',];

findMatchingRoutePath(routesObject, '/products/cool-shoes'); // => '/products/:product'
findMatchingRoutePath(routesArray, '/collections/luxury/products/fancy-watch'); // => '/collections/:collection/products/:product'
```

